import { useState } from 'react';
import './ProductList.css'
import CartItem from './CartItem';
import { useSelector, useDispatch } from 'react-redux';
import { addItem } from './CartSlice';

function ProductList() {
  const [showCart, setShowCart] = useState(false);
  const cart = useSelector((state) => state.cart);
  const dispatch = useDispatch();

  const plantsArray = [
    {
      category: "Low Maintenance",
      plants: [
        {
          name: "Snake Plant",
          image: "https://cdn.pixabay.com/photo/2021/01/22/06/04/snake-plant-5939187_1280.jpg",
          description: "Tolerates low light and infrequent watering.",
          cost: "$15"
        },
        {
          name: "Pothos",
          image: "https://cdn.pixabay.com/photo/2018/11/15/10/32/plants-3816945_1280.jpg",
          description: "Forgiving of neglect and comes in various colors.",
          cost: "$10"
        },
      ]
    },
    {
      category: "Flowering Beauty",
      plants: [
        {
          name: "African Violet",
          image: "data:image/jpeg;base64,",
          description: "Produces beautiful blooms in various colors.",
          cost: "$18"
        },
        {
          name: "Orchid",
          image: "data:image/jpeg;base64,",
          description: "Elegant and long-lasting blooms in diverse shapes.",
          cost: "$30"
        },
      ]
    },
    {
      category: "Unique & Interesting",
      plants: [
        {
          name: "Air Plant",
          image: "data:image/jpeg;base64,",
          description: "Requires no soil and adds a unique touch to any space.",
          cost: "$12"
        },
        {
          name: "Venus Flytrap",
          image: "data:image/jpeg;base64,",
          description: "A carnivorous plant that captures and digests insects.",
          cost: "$20"
        },
      ]
    }
  ];

  const styleObj = {
    backgroundColor: '#4CAF50',
    color: '#fff!important',
    padding: '15px',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    fontSize: '20px',
  };

  const styleA = {
    color: 'white',
    fontSize: '30px',
    textDecoration: 'none',
  };

  const handleCartClick = (e) => {
    e.preventDefault();
    setShowCart(true);
  };

  const handleContinueShopping = (e) => {
    e.preventDefault();
    setShowCart(false);
  };

  const handleAddToCart = (product) => {
    dispatch(addItem(product));
  };

  return (
    <div>
      <div className="navbar" style={styleObj}>
        <div className="tag">
          <div className="centered">
            <img src="data:image/jpeg;base64," alt="plant-logo" />
            <a href="/react-coursera-course/" style={{ textDecoration: 'none' }}>
              <div style={{ paddingLeft: '20px' }}>
                <h3 style={{ color: 'white' }}>Paradise Nursery</h3>
              </div>
            </a>
          </div>
        </div>
            <div>
                <a href="#" onClick={(e) => handleCartClick(e)} style={styleA}>
                <h1 className="cart">
                    <span>{cart.numOfItems}</span>
                    <img src="https://cdn-icons-png.flaticon.com/512/1413/1413908.png" alt="Shopping Cart" height="68" width="68" />
                </h1>
                </a>
            </div>
      </div>
      {!showCart ? (
        <div>
          {plantsArray.map((section, sectionIndex) => (
            <div className="product-grid" key={sectionIndex}>
              <h2 className="plant_heading">{section.category}</h2>
              <div className="product-list">
                {section.plants.map((plant, plantIndex) => (
                  <div className="product-card" key={plantIndex}>
                    <h3 className="product-title">{plant.name}</h3>
                    <img className="product-image" src={plant.image} alt={plant.name} />
                    <p className="product-price">{plant.cost}</p>
                    <p>{plant.description}</p>
                    {cart.items.some(item => item.name === plant.name) ? (
                      <button className="product-button added-to-cart">Added to Cart</button>
                    ) : (
                      <button className="product-button" onClick={() => handleAddToCart(plant)}>Add to Cart</button>
                    )}
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      ) : (
        <CartItem onContinueShopping={handleContinueShopping} />
      )}
    </div>
  );
}

export default ProductList;